values.value:
{
    type: 'object',
    description: 'A user dto',
    properties: {
        username: { type: 'string'},
        profile: {
            type: 'object',
            properties: {
                firstName: { type: 'string' },
                lastName: { type: 'string' },
                email: { type: 'string' },
                phone: { type: 'string' }
            },
            required: ['email']
        },
        joined: { type: 'string' }
    },
    required: [ 'username', 'joined', 'profile' ]
}

{
    type: 'object',
    properties: {
        id: { type: 'number' },
        $recursiveAnchor: true,
        author: {
            type: 'object',
            $ref: pm.environment.get('userSchema')
        },
        posted: { type: 'string' },
        content: { type: 'string' },
        inReplyTo: {
            type: 'object',
            $recursiveRef: '#'
        },
        repostOf: {
            type: 'object',
            $recursiveRef: '#'
        }
    },
    required: ['id', 'posted', 'author']
}

{
    type: 'object',
    properties: {
        label: { type: 'string' },
        firstUsed: { type: 'string' },
        lastUsed: { type: 'string' }
    },
    required: ['label', 'firstUsed', 'lastUsed']
}
